function openCanvas(element) {
    closeCanvas();
    $(".needs-validation").removeClass("was-validated");
    $('body').prepend('<div class="bs-canvas-overlay bg-dark position-fixed w-100 h-100 show"></div>');
    $(element).addClass('mr-0');
    $(element).addClass('show');
}

function closeCanvas() {
    $('.bs-canvas-right').removeClass('mr-0')
    $('.bs-canvas-overlay').remove();
    return false;
}
