$(".table-main").dataTable({
    columnDefs: [{ sortable: false }],
});

function clearInput() {
    $(":input", ".needs-validation")
        .not(":button, :submit, :reset, :hidden")
        .val("")
        .prop("selectedIndex", 0)
        .removeClass("is-invalid")
        .next(".invalid-feedback")
        .removeClass("d-block");
}

$(".form-control").keypress(function () {
    $(this).removeClass("is-invalid"),
        $(this).next(".invalid-feedback").removeClass("d-block");
});

$(".pull-bs-canvas-right").on("click", function () {
    openCanvas($(this).attr("data-target"));
});

$(".bs-canvas-close, .bs-canvas-overlay").on("click", function () {
    closeCanvas();
    clearInput();
});

var data_name = $(".dataName").val();

function funcEditForm(a) {
    openCanvas("#edit-" + data_name);
    $(".bs-canvas-overlay").removeClass("show");
    $(".form-control").removeClass("invalid");
    $(".invalid-feedback").removeClass("d-block");
}
